import { PopupWithForm } from "./PopupWithForm"
export function EditAvatarPopup(props) {
    return (
        <PopupWithForm name='avatar' title='Обновить аватар' isOpen={props.isOpen} onClose={props.onClose}>
            <input className="form__input form__input_avatar_link" type="url" placeholder="Ссылка на новый аватар"
                name="avatar" id="avatar" required />
            <span className="form__error avatar-error"></span>
        </PopupWithForm>
    )
}
