import React from "react"
import { PopupWithForm } from "./PopupWithForm"
import { UseFormValidation } from "../hooks/UseFormValidation";
export function EditAvatarPopup(props) {

    const { values, errors, isValid, handleChange, resetForm } = UseFormValidation({});

    React.useEffect(() => {
        resetForm();
    }, [props.isOpen, resetForm])
    
    const avatarRef = React.useRef();
    function handleSubmit(e) {
        e.preventDefault();

        props.onUpdateAvatar({
            avatar: avatarRef.current.value
        });
        avatarRef.current.value = ''
    }

    return (
        <PopupWithForm name='avatar' title='Обновить аватар' buttonName='Обновить аватар' isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleSubmit} >
            <input ref={avatarRef || ''} className="form__input form__input_avatar_link" type="url" placeholder="Ссылка на новый аватар"
                name="avatar" id="avatar" required />
            <span className="form__error avatar-error"></span>
        </PopupWithForm>
    )
}
