import React from "react"
import { PopupWithForm } from "./PopupWithForm";

export function EditProfilePopup(props) {
    const [name, setName] = React.useState('');
    const [profession, setProfession] = React.useState('');    

    function handleChangeName(e) {
        setName(e.target.value);
    }
    function handleChangeProfession(e) {
        setProfession(e.target.value);
    }

    
    return (
        <PopupWithForm name='edit' title='Редактировать профиль' isOpen={props.isOpen} onClose={props.onClose}>
            <input value={name} onChange={handleChange} className="form__input form__input_data_name" type="text" placeholder="Имя" name="name" id="name" required minLength="2" maxLength="40" />
            <span className="form__error name-error"></span>
            <input value={info.profession} onChange={handleChange} className="form__input form__input_data_profession" type="text" placeholder="О себе"
                name="profession" id="profession" required minLength="2" maxLength="200" />
            <span className="form__error profession-error"></span>
        </PopupWithForm>
    )
}
