import React, { useEffect } from 'react';
import { Header } from './Header.js';
import { Main } from './Main.js';
import { Footer } from './Footer.js';
import { PopupWithForm } from './PopupWithForm.js';
import { ImagePopup } from './ImagePopup.js';

function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [selectedCard, setSelectedCard] = React.useState(null)
    const [currentUser, setCurrentUser] = React.useState('');

    useEffect(() => {
        api.getUserInfo()
            .then((data) => {
                setUserName(data.name)
                setUserProfession(data.about)
                setUserAvatar(data.avatar)
            })
            .catch((err) => {
                console.log(`Ошибка: ${err}`)
            })
    }, [])

    function handleCardClick(card) {
        setSelectedCard(card);
    }

    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true);
    }

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true);
    }

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true);
    }

    function closeAllPopup() {
        setIsEditAvatarPopupOpen(false);
        setIsEditProfilePopupOpen(false);
        setIsAddPlacePopupOpen(false);
        setSelectedCard(null);
    }

    return (
        <div className="page">
            <Header />
            <Main onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onCardClick={handleCardClick} />
            <Footer />
            <PopupWithForm name='edit' title='Редактировать профиль' isOpen={isEditProfilePopupOpen} isClose={closeAllPopup}>
                <input className="form__input form__input_data_name" type="text" placeholder="Имя" name="name" id="name" required minLength="2" maxLength="40" />
                <span className="form__error name-error"></span>
                <input className="form__input form__input_data_profession" type="text" placeholder="О себе"
                    name="profession" id="profession" required minLength="2" maxLength="200" />
                <span className="form__error profession-error"></span>
            </PopupWithForm>

            <PopupWithForm name='add' title='Новое место' isOpen={isAddPlacePopupOpen} isClose={closeAllPopup}>
                <input className="form__input form__input_element_name" type="text" placeholder="Название"
                    name="name" id="element-name" required minLength="2" maxLength="30" />
                <span className="form__error element-name-error"></span>
                <input className="form__input form__input_element_link" type="url" placeholder="Ссылка на картинку"
                    name="link" id="element-link" required />
                <span className="form__error element-link-error"></span>
            </PopupWithForm>
            <PopupWithForm name='delete' title='Вы уверены?' />
            <PopupWithForm name='avatar' title='Обновить аватар' isOpen={isEditAvatarPopupOpen} isClose={closeAllPopup}>
                <input className="form__input form__input_avatar_link" type="url" placeholder="Ссылка на новый аватар"
                    name="avatar" id="avatar" required />
                <span className="form__error avatar-error"></span>
            </PopupWithForm>
            <ImagePopup card={selectedCard} onClose={closeAllPopup} />
        </div>
    );
}

export default App;
