import React from "react"
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import { PopupWithForm } from "./PopupWithForm";

export function EditProfilePopup(props) {
    const currentUser = React.useContext(CurrentUserContext);

    const { values, errors, isValid, handleChange, resetForm } = UseFormValidation({});



    React.useEffect(() => {
        if (currentUser) {
            resetForm(currentUser);
        }
    }, [currentUser, resetForm, props.isOpen])

    function handleSubmit(e) {
        e.preventDefault();
        props.onUpdateUser(values);
    }


    return (
        <PopupWithForm isDisabled={!isValid} name='edit' title='Редактировать профиль' buttonName='Сохранить' isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleSubmit}>
            <input value={values.name || ''} onChange={handleChangeName} className="form__input form__input_data_name" type="text" placeholder="Имя" name="name" id="name" required minLength="2" maxLength="40" />
            <span className="form__error name-error"></span>
            <input value={profession || ''} onChange={handleChangeProfession} className="form__input form__input_data_profession" type="text" placeholder="О себе"
                name="profession" id="profession" required minLength="2" maxLength="200" />
            <span className="form__error profession-error"></span>
        </PopupWithForm>
    )
}
