import React from "react"
import { PopupWithForm } from "./PopupWithForm"
export function AddPlacePopup(props) {
    const [name, setName] = React.useState('');
    const [link, setLink] = React.useState('');

    function handleChangeName(e) {
        setName(e.target.value);
    }
    function handleChangeLink(e) {
        setLink(e.target.value);
    }

    function handleSubmit(e) {
        e.preventDefault();
        props.onAddPlace({
            name: name,
            link: link,
        });
    }

    function handleClear(e) {
        e.preventDefault();
        setName('');
        setLink('');
    }

    return(
        <PopupWithForm name='add' title='Новое место' isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleSubmit} handleClear={handleClear}>
                    <input value={name || ''} onChange={handleChangeName} className="form__input form__input_element_name" type="text" placeholder="Название"
                        name="name" id="element-name" required minLength="2" maxLength="30" />
                    <span className="form__error element-name-error"></span>
                    <input value={link || ''} onChange={handleChangeLink} className="form__input form__input_element_link" type="url" placeholder="Ссылка на картинку"
                        name="link" id="element-link" required />
                    <span className="form__error element-link-error"></span>
                </PopupWithForm>
    )
}
