import avatar from '../images/Avatar.png';
import pencilAvatar from '../images/edit_avatar.png';

export function Main() {

    function handleEditAvatarClick() {
        document.querySelector('.popup-avatar').classList.add('popup_opened')
    }

    function handleEditProfileClick() {
        document.querySelector('.popup-edit').classList.add('popup_opened')
    }

    function handleAddPlaceClick() {
        document.querySelector('.popup-add').classList.add('popup_opened')
    }
    return(
      <main className="content">
        <section className="profile">
            <div className="profile__avatar">
                <img className="profile__avatar-image" src={avatar} alt="Аватар пользователя"/>
                <div onClick={handleEditAvatarClick} className="profile__overlay">
                    <img className="profile__avatar-edit" src={pencilAvatar} alt="Иконка редактирования"/>
                </div>
            </div>
            <div className="profile__info">
                <h1 className="profile__name">Жак-Ив Кустов</h1>
                <p className="profile__profession">Исследователь океана</p>
                <button onClick={handleEditProfileClick} className="profile__info-change-button" type="button" aria-label="Изменить профиль"></button>
            </div>
            <button onClick={handle} className="profile__add-button" type="button"></button>
        </section>
        <section className="elements">
            <ul className="elements__items">
            </ul>
        </section>
      </main>
    )
}
