import { handle } from "express/lib/application";
import React from "react"

export function EditProfilePopup(props) {
    const [info, setInfo] = React.useState({
        name: '',
        profession: ''
    });

    function handleChange(e) {
        setInfo({[e.target.name]: e.target.value});
    }

    
    return (
        <PopupWithForm name='edit' title='Редактировать профиль' isOpen={props.isOpen} onClose={props.onClose}>
            <input value={info.name} onChange={handleChange} className="form__input form__input_data_name" type="text" placeholder="Имя" name="name" id="name" required minLength="2" maxLength="40" />
            <span className="form__error name-error"></span>
            <input className="form__input form__input_data_profession" type="text" placeholder="О себе"
                name="profession" id="profession" required minLength="2" maxLength="200" />
            <span className="form__error profession-error"></span>
        </PopupWithForm>
    )
}
