import React, { useEffect } from 'react';
import pencilAvatar from '../images/edit_avatar.png';
import { api } from '../utils/Api';
import Card from '../../../mesto/src/components/Card';

export function Main(props) {
    const [userName, setUserName] = React.useState('');
    const [userProfession, setUserProfession] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');
    const [cards, setCards] = React.useState([]);

    useEffect(() => {
        api.getUserInfo()
            .then((data) => {
                setUserName(data.name)
                setUserProfession(data.about)
                setUserAvatar(data.avatar)
            })
            .catch((err) => {
                console.log(`Ошибка: ${err}`)
            })
    }, [userName, userAvatar, userProfession])

    useEffect(() => {
        api.getInitialCards()
            .then((data) => {
                setCards(data)
            })
            .catch((err) => {
                console.log(`Ошибка: ${err}`)
            })
    }, [cards])
    return (
        <main className="content">
            <section className="profile">
                <div className="profile__avatar">
                    <img className="profile__avatar-image" src={userAvatar} alt="Аватар пользователя" />
                    <div onClick={props.onEditAvatar} className="profile__overlay">
                        <img className="profile__avatar-edit" src={pencilAvatar} alt="Иконка редактирования" />
                    </div>
                </div>
                <div className="profile__info">
                    <h1 className="profile__name">{userName}</h1>
                    <p className="profile__profession">{userProfession}</p>
                    <button onClick={props.onEditProfile} className="profile__info-change-button" type="button" aria-label="Изменить профиль"></button>
                </div>
                <button onClick={props.onAddPlace} className="profile__add-button" type="button"></button>
            </section>
            <section className="elements">
                <ul className="elements__items">
                    {cards.map((card) => (
                        <li className="elements__item" key={card._id}>
                        <img className="elements__image" src={card.link} alt={card.name} />
                        <div className="elements__group">
                            <h2 className="elements__title">{card.name}</h2>
                            <div className="elements__like-container">
                                <button className="elements__like-button" type="button" aria-label="Лайкнуть фото"></button>
                                <span className="elements__like-counter">{card.likes.length}</span>
                            </div>
                        </div>
                        <button className="elements__delete-button" aria-label="удалить фотографию"></button>
                    </li>
                    ))}
                </ul>
            </section>
        </main>
    )
}
